import React,{useState, useEffect} from 'react'
import { MapContainer, TileLayer, Marker } from 'react-leaflet';
import {Card, Table, Pagination} from 'react-bootstrap';
function Restaurant(props){
    const [restaurant, setRestaurant] = useState(null);
    const [loading, setLoading] = useState(true);
    useEffect(() => {
        setLoading(true);
        fetch(`https://web422ass1.herokuapp.com/api/restaurants/:id`)
        .then(res=>res.json)
        .then(data=>{
            setLoading(false);
            if(data.hasOwnProperty("_id")){
                setRestaurant(data)
            }else{
                setRestaurant(null);
            }
        })
    }, [props.match.params.id]);
    return(
        <div>
            <Card>
                <Card.Header>
                    {restaurant.name}
                    <Card.Text>
                    {restaurant.address.building} {restaurant.address.street}
                    </Card.Text>
                </Card.Header>
            </Card>

            <MapContainer style={{"height": "400px"}} center={[restaurant.address.coord[1], restaurant.address.coord[0]]} zoom={13} scrollWheelZoom={false}>
     <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
     <Marker position={[ restaurant.address.coord[1], restaurant.address.coord[0]]}></Marker>
</MapContainer>


        </div>
    )
}

export default Restaurant;
