import React,{useState, useEffect} from 'react'
import { MapContainer, TileLayer, Marker } from 'react-leaflet';
import {Card, CardDeck} from 'react-bootstrap';
function Restaurant(props){
    const [restaurant, setRestaurant] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        setLoading(true);
        fetch(`https://web422ass1.herokuapp.com/api/restaurants/${props.id}`)
        .then(res=>res.json())
        .then(result=>{
            console.log("result" + result);
            setLoading(false);
            if(result.hasOwnProperty("_id")){
                alert('test');
               setRestaurant(result);
            }else{
                setRestaurant(null);
            }
        })
     }, [props.id]);

    if(loading===true){
        return(
            <Card>
        <Card.Header>
            <Card.Text>
                Loading Restaurant Data...
            </Card.Text>
        </Card.Header>
    </Card>
        )
        
    }
    else if(!restaurant){
        return(
            <Card>
        <Card.Header>
            <Card.Text>
               Unable to find Restaurant with id: {restaurant._id}
            </Card.Text>
        </Card.Header>
    </Card>
        )
        
    }
    else{
        return(
             <div>
                 <Card>
                     <Card.Header>
                      {restaurant.name} 
                         <Card.Text>
                    {restaurant.address.building} {restaurant.address.street} 
                         </Card.Text>
                     </Card.Header>
                 </Card>
    
                <MapContainer style={{"height": "400px"}} center={[restaurant.address.coord[1], restaurant.address.coord[0]]} zoom={13} scrollWheelZoom={false}>
                     <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                     <Marker position={[ restaurant.address.coord[1], restaurant.address.coord[0]]}></Marker>
                 </MapContainer>
    <br></br>
    <br></br>
 <CardDeck>
     <h1>Ratings</h1>
  <Card>
    <Card.Body>
      <Card.Title>Card title</Card.Title>
      <Card.Text>
        This is a wider card with supporting text below as a natural lead-in to
        additional content. This content is a little bit longer.
      </Card.Text>
    </Card.Body>
    <Card.Footer>
      <small className="text-muted">Last updated 3 mins ago</small>
    </Card.Footer>
  </Card>
  <Card>
    <Card.Img variant="top" src="holder.js/100px160" />
    <Card.Body>
      <Card.Title>Card title</Card.Title>
      <Card.Text>
        This card has supporting text below as a natural lead-in to additional
        content.{' '}
      </Card.Text>
    </Card.Body>
    <Card.Footer>
      <small className="text-muted">Last updated 3 mins ago</small>
    </Card.Footer>
  </Card>
  <Card>
    <Card.Img variant="top" src="holder.js/100px160" />
    <Card.Body>
      <Card.Title>Card title</Card.Title>
      <Card.Text>
        This is a wider card with supporting text below as a natural lead-in to
        additional content. This card has even longer content than the first to
        show that equal height action.
      </Card.Text>
    </Card.Body>
    <Card.Footer>
      <small className="text-muted">Last updated 3 mins ago</small>
    </Card.Footer>
  </Card>
</CardDeck>

             </div>
        )
    }
    
}

export default Restaurant;
