import React from 'react';
import './App.css';
import {Route, Switch} from 'react-router-dom';
import { LinkContainer } from 'react-router-bootstrap';
import { Navbar, Nav, NavItem } from 'react-bootstrap';
import Restaurants from './Restaurants';
import Restaurant from './Restaurant';
import About from './About';
import NotFound from './NotFound';

function App() {
  return (
    <div>
    <Navbar inverse collapseOnSelect fixedTop>
      <Navbar.Header>
        <LinkContainer to="/">
          <Navbar.Brand>
            WEB422 - Assignment 3
          </Navbar.Brand>
        </LinkContainer>
        <Navbar.Toggle/>
      </Navbar.Header>
      <Navbar.Collapse>
        <Nav>
          <LinkContainer to="/Restaurants">
            <NavItem eventKey={1}>Restaurants</NavItem>
          </LinkContainer>
          <LinkContainer to="/Restaurant">
            <NavItem eventKey={1}>Restaurant</NavItem>
          </LinkContainer>
          <LinkContainer to="/About">
            <NavItem eventKey={2}>About</NavItem>
          </LinkContainer>
          <LinkContainer to="/NotFound">
            <NavItem eventKey={1}>NotFound</NavItem>
          </LinkContainer>
        </Nav>
      </Navbar.Collapse>
    </Navbar>

    <br /><br /><br />

    <div className="container">
      <div className="row">
        <div className="col-md-12">

{/* this will route to the main products page */}
          <Switch>
            <Route exact path='/Restaurants' render={() => (
              <Restaurants />
            )}/>
{/* this will route to the product with a selected id */}
            <Route exact path='/Restaurant' render={() => (
              <Restaurant />
            )}/>
{/* this will route to the about page */}
              <Route exact path='/About' render={() => (
                <About />
              )} />
              <Route exact path='/NotFound' render={() => (
              <NotFound />
            )}/>

          </Switch>
        </div>
      </div>
    </div>
  </div>
  );
}

export default App;
