import React,{useState, useEffect} from 'react';
import './App.css';
import { Button, Col, Container, Form, FormControl, Nav, Navbar, Row } from 'react-bootstrap';
import { Redirect, Route, Switch, Router,useHistory } from 'react-router-dom';
import { LinkContainer } from 'react-router-bootstrap';
import Restaurants from './Restaurants';
import Restaurant from './Restaurant';
import About from './About';
import NotFound from './NotFound';


function App() {
  const [searchString, setSearchString] = useState("");
  let history = useHistory();
  const  handleSubmit=(e)=>{
    e.preventDefault();
    history.push(`/restaurants?borough=${e.searchString} `)
    setSearchString("");
  }
  return (
    <div>

      <>
  <Navbar bg="light" expand="lg">
    <LinkContainer to="/">
      <Navbar.Brand>New York Restaurants</Navbar.Brand>
    </LinkContainer>
    <Navbar.Toggle aria-controls="basic-navbar-nav" />
    <Navbar.Collapse id="basic-navbar-nav">
      <Nav className="mr-auto">
        <LinkContainer to="/restaurants">
          <Nav.Link>Full List</Nav.Link>
        </LinkContainer>
        <LinkContainer to="/about">
          <Nav.Link>About</Nav.Link>
        </LinkContainer>
      </Nav>
      <Form onSubmit={handleSubmit} inline>
        <FormControl type="text" placeholder="Borough" className="mr-sm-2" value={searchString} onChange={(e) => setSearchString(e.target.value)} />
        <Button type="submit" variant="outline-success">Search</Button>
      </Form>
    </Navbar.Collapse>
  </Navbar>
  <br />
</>

<Container>
  <Row>
    <Col>
      <Switch>

        {/* 1. Route "/"" */}
        <Route exact path='/' render={() => (
          <Restaurants />
        )}/>
       <Route exact path='/about' render={() => (
        <About />
        )}/>
      <Route path='/Restaurants' render={(props) => (
       <Restaurants query={searchString} />
        )}/>
       <Route path='/Restaurant/:id' render={(props) => (
        <Restaurants id={props.match.params.id}/>
        )}/>
        <Route exact path='/NotFound' render={() =>{
          <NotFound/>
        }}/>


      </Switch>
    </Col>
  </Row>
</Container>

    </div>
  );
}

export default App;
