import React,{useState, useEffect} from 'react';
import queryString from 'query-string';
import {Card, Table} from 'react-bootstrap';

function Restaurants(props){
    const [restaurants, setRestaurants] = useState(null);
    const [page, setPage] = useState(1);

     useEffect(() => {
        fetch(`https://web422ass1.herokuapp.com/api/restaurants?page=${props.page}&perPage=10&borough=${props.query}`)
        .then(res=>res.json())
        .then(result =>{
            setRestaurants(result);
        })
     }, [props.page, props.query ]);

     const previousPage = ()=>{
         if(page>0){
             setPage(page-1)
         }
     const nextPage = ()=>{
         setPage(page+1);
        }
        
     }

    return (
        <div>
        <Card>
        <Card.Header>
          About
          <Card.Text>
            Muhammed Shijas
          </Card.Text>
          </Card.Header>
      </Card>

<Table striped bordered hover>
<thead>
  <tr>
    <th>Name</th>
    <th>Address</th>
    <th>Borough</th>
    <th>Cuisine</th>
  </tr>
</thead>
<tbody>
  <tr>
    <td>1</td>
    <td>Mark</td>
    <td>Otto</td>
    <td>@mdo</td>
  </tr>
  <tr>
    <td>2</td>
    <td>Jacob</td>
    <td>Thornton</td>
    <td>@fat</td>
  </tr>
  <tr>
    <td>3</td>
    <td colSpan="2">Larry the Bird</td>
    <td>@twitter</td>
  </tr>
</tbody>
</Table>

</div>
    )
}

export default Restaurants;
