import React,{useState, useEffect} from 'react'
import { MapContainer, TileLayer, Marker } from 'react-leaflet';
import {Card, Table, Pagination} from 'react-bootstrap';
function Restaurant(props){
    const [restaurant, setRestaurant] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        
        fetch(`https://web422ass1.herokuapp.com/api/restaurants/${props.id}`)
        .then(res=>res.json())
        .then(result =>{
            console.log(result);
            setRestaurants(result);
        })
     }, [page, props.query]);
    useEffect(() => {
        setLoading(true);
        fetch(`https://web422ass1.herokuapp.com/api/restaurants/5eb3d668b31de5d588f4297e`)
        .then(res=>res.json)
        .then(result=>{
            alert('test');
            setLoading(false);
            if(result.data.hasOwnProperty("_id")){
               setRestaurant(result.data);
            }else{
                setRestaurant(null);
            }
        })
    }, [props.id]);
    if(loading===true){
        <Card>
        <Card.Header>
            <Card.Text>
                Loading Restaurant Data...
            </Card.Text>
        </Card.Header>
    </Card>
    }
    else if(!restaurant){
        <Card>
        <Card.Header>
            <Card.Text>
               Unable to find Restaurant with id: 
            </Card.Text>
        </Card.Header>
    </Card>
    }
    return(
        <div>
            <Card>
                <Card.Header>
                    {restaurant.name}
                    <Card.Text>
                    {restaurant.address.building} {restaurant.address.street}
                    </Card.Text>
                </Card.Header>
            </Card>

            <MapContainer style={{"height": "400px"}} center={[restaurant.address.coord[1], restaurant.address.coord[0]]} zoom={13} scrollWheelZoom={false}>
                <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                <Marker position={[ restaurant.address.coord[1], restaurant.address.coord[0]]}></Marker>
            </MapContainer>

            
        </div>
    )
}

export default Restaurant;
