import React,{useState, useEffect} from 'react';
import './App.css';
import { Button, Col, Container, Form, FormControl, Nav, Navbar, Row } from 'react-bootstrap';
import { Redirect, Route, Switch, useHistory, Router } from 'react-router-dom';
import { LinkContainer } from 'react-router-bootstrap';

function App() {
  const [searchString, setSearchString] = useState("");
  const  handleSubmit=(e)=>{
    e.preventDefault();
    function HomeButton() {
      let history = useHistory();    
      function handleClick() {
        history.push(`/restaurants?borough=${e.searchString}`);
      }    
      return (
        {handleClick}
      );
    }
    e.searchString="";
  }
  return (
    <div>
      <Router>
      <>
  <Navbar bg="light" expand="lg">
    <LinkContainer to="/">
      <Navbar.Brand>New York Restaurants</Navbar.Brand>
    </LinkContainer>
    <Navbar.Toggle aria-controls="basic-navbar-nav" />
    <Navbar.Collapse id="basic-navbar-nav">
      <Nav className="mr-auto">
        <LinkContainer to="/restaurants">
          <Nav.Link>Full List</Nav.Link>
        </LinkContainer>
        <LinkContainer to="/about">
          <Nav.Link>About</Nav.Link>
        </LinkContainer>
      </Nav>
      <Form onSubmit={handleSubmit} inline>
        <FormControl type="text" placeholder="Borough" className="mr-sm-2" value={searchString} onChange={(e) => setSearchString(e.target.value)} />
        <Button type="submit" variant="outline-success">Search</Button>
      </Form>
    </Navbar.Collapse>
  </Navbar>
  <br />
</>
</Router>
    </div>
  );
}

export default App;
