import React,{useState, useEffect} from 'react';
import queryString from 'query-string';
import {Card, Table, Pagination} from 'react-bootstrap';

function Restaurants(props){
    const [restaurants, setRestaurants] = useState(null);
    const [page, setPage] = useState(1);

    // useEffect(() => {
    //     setRestaurants({
    //         address: null,
    //         _id: "5eb3d668b31de5d588f4299a",
    //         borough: "Bronx",
    //         cuisine: "American",
    //         grades: null,
    //         name: "Yankee Tavern",
    //         restaurant_id:"40365499"
    //     })
    // }, []);


     useEffect(() => {
        queryString.parse(props.query)
        fetch(`https://web422ass1.herokuapp.com/api/restaurants?page=${page}&perPage=10&borough=${props.query}`)
        .then(res=>res.json())
        .then(result =>{
            console.log(result);
            console.log(query.props);
            
            return(setRestaurants(result));
            
        })
     }, [page, props.query]);
function genRestaurant(){
    
        restaurants.map((rest) => {
            return(
                <tr data-id={rest._id} >
                <td> {rest.name} </td>
                <td> {rest.address.building} {rest.address.street} </td>
                <td> {rest.borough} </td>
                <td> {rest.cuisine} </td>
            </tr>
            )}
    )
}

     const previousPage = ()=>{
         if(page>0){
             setPage(page-1);
         }
 };

 const nextPage = ()=>{
    setPage(page+1);
}
     if (!restaurants){
        return (
            <Card>
                <Card.Header>
                    <Card.Text>
                        Loading Restaurants...
                    </Card.Text>
                </Card.Header>
            </Card>
        )
    }
    else if(restaurants == null){
        <Card>
                <Card.Header>
                    <Card.Text>
                        No Restaurants Found
                    </Card.Text>
                </Card.Header>
            </Card>
    }
    else {
    return (
        
            <div>
        
        <Card>
        <Card.Header>
          Restaurant List
          <Card.Text>
            Full list of restaurants. Optionally sorted by borough
          </Card.Text>
          </Card.Header>
      </Card>
      
<Table striped bordered hover>
<thead>
  <tr>
    <th>Name</th>
    <th>Address</th>
    <th>Borough</th>
    <th>Cuisine</th>
  </tr>
</thead>
<tbody>
    {genRestaurant}
</tbody>
</Table>

<Pagination>
    <Pagination.Prev onClick={previousPage}/>
    <Pagination.Item>{page}</Pagination.Item>
    <Pagination.Next onClick={nextPage} />
</Pagination>

</div>
        
    )
}
}

export default Restaurants;
