import React,{useState, useEffect} from 'react';
import queryString from 'query-string';
import {Card, Table} from 'react-bootstrap';

function Restaurants(props){
    const [restaurants, setRestaurants] = useState(null);
    const [page, setPage] = useState(1);

     useEffect(() => {
        fetch(`https://web422ass1.herokuapp.com/api/restaurants?page=${props.page}&perPage=10&borough=${props.query}`)
        .then(res=>res.json())
        .then(result =>{
            setRestaurants(result);
        })
     }, [props.page, props.query ]);
function genRestaurant(){
    return(
        restaurants.map((rest)=>{
<tr data-id={rest._id} >
                <td> {rest.name} </td>
                <td> {rest.address.building} {rest.address.street} </td>
                <td> {rest.Borough} </td>
                <td> {rest.cuisine} </td>
            </tr>
        })
    )
}

     const previousPage = ()=>{
         if(page>0){
             setPage(page-1)
         }
     const nextPage = ()=>{
         setPage(page+1);
        }
        
     }

    return (
        <div>
        <Card>
        <Card.Header>
          About
          <Card.Text>
            Muhammed Shijas
          </Card.Text>
          </Card.Header>
      </Card>

<Table striped bordered hover>
<thead>
  <tr>
    <th>Name</th>
    <th>Address</th>
    <th>Borough</th>
    <th>Cuisine</th>
  </tr>
</thead>
<tbody>
    {genRestaurant()}
</tbody>
</Table>

</div>
    )
}

export default Restaurants;
